import React from "react";
import { BrowserRouter as Router, Route, Routes, useLocation } from "react-router-dom";
import EmployeeRegister from "./Components/EmployeeRegister";
import Profile from "./Components/Profile";
import EmployeeList from "./Components/EmployeeList";
import Sidebar from "./Components/Sidebar";

function Layout() {
  const location = useLocation();
  const hideSidebarRoutes = ["/"]; // Add more routes if needed

  return (
    <div style={{ display: "flex" }}>
      {/* Show Sidebar only if the current route is NOT in hideSidebarRoutes */}
      {!hideSidebarRoutes.includes(location.pathname) && <Sidebar />}

      {/* Main Content */}
      <div style={{ marginLeft: !hideSidebarRoutes.includes(location.pathname) ? "60px" : "0", flexGrow: 1, padding: "20px" }}>
        <Routes>
          <Route path="/" element={<EmployeeRegister />} />
          <Route path="/Profile" element={<Profile />} />
          <Route path="/EmployeeList" element={<EmployeeList />} />
        </Routes>
      </div>
    </div>
  );
}

function App() {
  return (
    <Router>
      <Layout />
    </Router>
  );
}

export default App;
